<!DOCTYPE  html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">

<head>
   <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>
   <title>Salary Slip</title>

   <style type="text/css">
      * { margin:0; padding:0; }
      body { font-family: "Times New Roman"; }

      .main-table {
         border-collapse: collapse;
         margin: 20px auto;
         width: 750px;
      }

      table, td {
         border: 1px solid black;
      }

      td {
         font-size: 12pt;
         padding: 4px;
      }

      .s5 { font-weight: bold; }
      .s4 { font-weight: bold; font-size: 14pt; }
   </style>

</head>

<body>

   <!-- MAIN TABLE START -->
   <table class="main-table">

      <!-- LOGO + COMPANY NAME SIDE BY SIDE -->
      <tr>
         <td colspan="4" style="padding:10px;">
            <table style="width:100%; border:0; border-collapse:collapse;">
               <tr>

                  <!-- LOGO LEFT -->
                  <td style="width:100px; text-align:left; border:0;">
                     <img src="data:image/png;base64,"
                          style="width:90px; height:90px;">
                  </td>

                  <!-- COMPANY INFO RIGHT -->
                  <td style="text-align:left; border:0;">
                     <span style="font-size:20pt; font-weight:bold; white-space:nowrap;">
                        TeamMP Research Work Pvt Ltd.
                     </span><br>
                     SM Tower, Plot No.-13, Nagar Panchvati, OM Nagar Colony, Masani Bypass Link Road,<br>
                     Behind National Chamber, Mathura (U.P)-281003<br>
                     Email: hr@mprw.in
                  </td>

               </tr>
            </table>
         </td>
      </tr>

      <!-- TITLE -->
      <td colspan="4" style="text-align:center; font-size:16pt; font-weight:bold;">
          Pay Slip for the Month of 
         <span style="text-transform:capitalize;">
           {{ \Carbon\Carbon::create()->month($monthRecord->month)->format('F') }}
         </span>
         {{ $monthRecord->year }}
      </td>


      <!-- EMPLOYEE DETAILS -->
      <tr>
         <td class="s5">Employee Name</td>
         <td>{{ $monthRecord->user->name }}</td>
         <td class="s5">Standard Working Days</td>
         <td style="text-align:center;">30</td>
      </tr>

      <tr>
         <td class="s5">Employee Code</td>
         <td>{{ $monthRecord->user->employee_code }}</td>
         <td class="s5">Actual Presence</td>
         <td style="text-align:center;">{{ $monthRecord->working_days }}</td>
      </tr>

      <tr>
         <td class="s5">Date of Joining</td>
         <td>{{ $monthRecord->user->profile->joining_date }}</td>
         <td class="s5">Total CL</td>
         <td style="text-align:center;">{{ $monthRecord->total_cl }}</td>
      </tr>

      <tr>
         <td class="s5">Department</td>
         <td>{{ $monthRecord->user->department->name }}</td>
         <td class="s5">No. of Leaves Taken</td>
         <td style="text-align:center;">{{ $monthRecord->leaves }}</td>
      </tr>

      <tr>
         <td class="s5">Designation</td>
         <td>{{ $monthRecord->user->designation->name }}</td>
         <td class="s5">No. of Half Days Taken</td>
         <td style="text-align:center;">{{ $monthRecord->half_days }}</td>
      </tr>

      <tr>
         <td class="s5">PAN</td>
         <td>{{$monthRecord->user->profile->pan}}</td>
         <td class="s5">Total WFH</td>
         <td style="text-align:center;">{{ $monthRecord->wfh }}</td>
      </tr>

      <tr>
         <td class="s5">Bank Account Number</td>
         <td>{{ $monthRecord->user->profile->acc_no }}</td>
         <td></td>
         <td></td>
      </tr>

      <tr><td colspan="4" style="height:10px;"></td></tr>

      <!-- INCOME / DEDUCTION SECTION -->
      <tr>
         <td colspan="2" class="s4" style="text-align:center;">Income</td>
         <td colspan="2" class="s4" style="text-align:center;">Deductions</td>
      </tr>

      <tr>
         <td class="s5" style="text-align:center;">Particulars</td>
         <td class="s5" style="text-align:center;">Amount</td>
         <td class="s5" style="text-align:center;">Particulars</td>
         <td class="s5" style="text-align:center;">Amount</td>
      </tr>

      <tr>
         <td class="s5">Gross Salary</td>
         <td style="text-align:center;">{{ number_format($monthRecord->gross_salary, 2) }}</td>
         <td>Loss on Excess Leaves</td>
         <td style="text-align:center;">{{ number_format($monthRecord->leave_deduction, 2) }}</td>
      </tr>

      <tr>
         <td>Basic Salary</td>
         <td style="text-align:center;">{{ number_format($monthRecord->basic, 2) }}</td>
         <td>Loss on WFH</td>
         <td style="text-align:center;">{{ number_format($monthRecord->wfh_deduction_cost, 2) }}</td>
      </tr>

      <tr>
         <td>House Rent Allowance</td>
         <td style="text-align:center;">{{ number_format($monthRecord->hra, 2) }}</td>
         <td>Loss on Half Days</td>
         <td style="text-align:center;">{{ number_format($monthRecord->half_day_deduction, 2) }}</td>
      </tr>

      <tr>
         <td>Special Allowance</td>
         <td style="text-align:center;">{{ number_format($monthRecord->simple_allowance, 2) }}</td>
         <td>Alternative Variable Losses</td>
         <td style="text-align:center;">{{ number_format($monthRecord->alternative_variable_loses, 2) }}</td>
      </tr>

      <tr>
         <td>Conveyance Allowance</td>
         <td style="text-align:center;">{{ number_format($monthRecord->conveyance, 2) }}</td>
         <td>PF</td>
         <td style="text-align:center;">{{ number_format($monthRecord->pf, 2) }}</td>
      </tr>

      <tr>
         <td>Other Allowance</td>
         <td style="text-align:center;">{{ number_format($monthRecord->other_allowance, 2) }}</td>
         <td>TDS</td>
         <td style="text-align:center;">{{ number_format($monthRecord->tds, 2) }}</td>
      </tr>

      <tr>
         <td>Alternative Variable Pay</td>
         <td style="text-align:center;">{{ number_format($monthRecord->alternative_variable_pay, 2) }}</td>
         <td></td>
         <td></td>
      </tr>

      <tr>
         <td>Incentive / Reward</td>
         <td style="text-align:center;">{{ number_format($monthRecord->reward + $monthRecord->incentive, 2) }}</td>
         <td></td>
         <td></td>
      </tr>

      <tr>
         <td class="s5">Total Earnings</td>
         <td style="text-align:center;">{{ number_format($monthRecord->total_earning, 2) }}</td>
         <td class="s5">Total Deduction</td>
         <td style="text-align:center;">{{ number_format($monthRecord->total_deduction, 2) }}</td>
      </tr>

      <tr>
         <td colspan="3" class="s4" style="padding-left:40px;">Net Pay</td>
         <td class="s4" style="text-align:center;">{{ number_format($monthRecord->net_salary, 2) }}</td>
      </tr>

   </table>

   <p style="text-align:center; margin-top:20px;">
      This is a computer-generated payslip and does not require a signature.
   </p>

</body>
</html>
